<template>
    <div class="mhy-rocket" v-if="showBtn" @click="backTop">
      <img src="data:image/png;base64," alt="">
    </div>
</template>

<script>
export default {
  name: 'Totop',
  data () {
    return {
      // 返回顶部状态
      showBtn: false
    }
  },
  methods: {
    showbtn () {
      const that = this
      const scrollTop =
          window.pageYOffset ||
          document.documentElement.scrollTop ||
          document.body.scrollTop
      that.scrollTop = scrollTop
      if (scrollTop > 900) {
        this.showBtn = true
      } else {
        this.showBtn = false
      }
    },
    backTop () {
      const timer = setInterval(function () {
        const osTop =
              document.documentElement.scrollTop || document.body.scrollTop
        const ispeed = Math.floor(-osTop / 5)
        document.documentElement.scrollTop = document.body.scrollTop =
              osTop + ispeed
        this.isTop = true
        if (osTop === 0) {
          clearInterval(timer)
        }
      }, 30)
    }
  },
  mounted () {
    window.addEventListener('scroll', this.showbtn, true)
  }
}
</script>

